// Fonction pour générer l'URL de l'image basée sur le SKU
export function getImageUrl(sku, _brand, name = '') {
  if (!sku) return null

  // D'abord, vérifier si on a une URL directe dans le map
  if (SNEAKER_IMAGES[sku]) {
    return SNEAKER_IMAGES[sku]
  }

  // Sinon, construire une URL basée sur le nom du modèle (format StockX)
  if (name) {
    const cleanName = name
      .replace(/'/g, '')
      .replace(/\s+/g, '-')
      .replace(/[^a-zA-Z0-9-]/g, '')
    return `https://images.stockx.com/images/${cleanName}-Product.jpg?fit=fill&bg=FFFFFF&w=300&h=214&fm=webp&auto=compress&q=90`
  }

  return null
}

// Fonction de recherche
export function searchSneakers(query) {
  if (!query || query.length < 2) return []

  const searchTerm = query.toLowerCase().trim()

  return SNEAKERS_DATABASE.filter(sneaker => {
    return (
      sneaker.name.toLowerCase().includes(searchTerm) ||
      sneaker.sku.toLowerCase().includes(searchTerm) ||
      sneaker.brand.toLowerCase().includes(searchTerm) ||
      sneaker.colorway?.toLowerCase().includes(searchTerm)
    )
  }).slice(0, 20).map(sneaker => ({
    ...sneaker,
    imageUrl: getImageUrl(sneaker.sku, sneaker.brand, sneaker.name)
  })) // Limite à 20 résultats et ajoute l'image
}
